import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Button from '../components/Button';
import TextInput from '../components/TextInput';

const SessionForm = ({ setIsFormCompleted }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    sessionName: '',
    description: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Set the form as completed
    setIsFormCompleted(true);
    navigate('/session');  // After form submission, go to the session page
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-[#0a192f] to-[#4a148c]">
      <div className="w-full max-w-md p-8 bg-black/40 backdrop-blur-lg rounded-2xl border border-white/10 shadow-xl">
        <h2 className="text-3xl font-bold text-center mb-8 text-white">
          Session Form
        </h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <label className="block text-sm font-medium text-blue-200">
              Session Name
            </label>
            <TextInput
              type="text"
              name="sessionName"
              value={formData.sessionName}
              onChange={handleChange}
              placeholder="Enter session name"
              required
            />
          </div>

          <div className="space-y-2">
            <label className="block text-sm font-medium text-blue-200">
              Session Description
            </label>
            <TextInput
              type="text"
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Enter session description"
              required
            />
          </div>

          <Button
            variant="submit"
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg
                       transition-all duration-300 transform hover:-translate-y-0.5"
          >
            Submit Session
          </Button>
        </form>
      </div>
    </div>
  );
};

export default SessionForm;
