import React, { useState } from 'react';

const SessionForm = ({ onSubmit }) => {
    const [topic, setTopic] = useState('');
    const [members, setMembers] = useState(1);
    const [additionalInfo, setAdditionalInfo] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit({ topic, members, additionalInfo });
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
            <h2 className="text-2xl font-bold">Brainstorming Session</h2>
            <form onSubmit={handleSubmit} className="mt-4">
                <div className="mb-4">
                    <label className="block text-lg">Topic:</label>
                    <input
                        className="border p-2 rounded w-full"
                        type="text"
                        value={topic}
                        onChange={(e) => setTopic(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-lg">Number of Members:</label>
                    <input
                        className="border p-2 rounded w-full"
                        type="number"
                        min="1"
                        value={members}
                        onChange={(e) => setMembers(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-lg">Additional Information:</label>
                    <textarea
                        className="border p-2 rounded w-full"
                        value={additionalInfo}
                        onChange={(e) => setAdditionalInfo(e.target.value)}
                    />
                </div>
                <button
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                    type="submit"
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default SessionForm;
