import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Button from '../components/Button';
import TextInput from '../components/TextInput';
import Loader from '../components/Loader';

const SessionForm = ({ onSubmit }) => {
    const [topic, setTopic] = useState('');
    const [members, setMembers] = useState(1);
    const [additionalInfo, setAdditionalInfo] = useState('');
    const [loading, setLoading] = useState(false); // State to handle loading

    const handleSubmit = (e) => {
        e.preventDefault();
        setLoading(true); // Start loading when the form is submitted

        // You might want to handle loading asynchronously in a real app
        setTimeout(() => {
            onSubmit({ topic, members, additionalInfo });
            setLoading(false); // Stop loading after submission
        }, 1000); // Simulate a delay for loading (remove in production)
    };

    return (
        <div className="flex flex-col items-center justify-between h-screen bg-gray-100">
            {/* Add Header component */}
            <Header />

            {/* Form section */}
            <div className="flex flex-col items-center justify-center flex-grow">
                <h2 className="text-2xl font-bold">Brainstorming Session</h2>

                <form onSubmit={handleSubmit} className="mt-4 w-full max-w-lg">
                    {/* Use TextInput component for the topic */}
                    <TextInput
                        label="Topic"
                        value={topic}
                        onChange={setTopic}
                        required
                    />

                    {/* Use TextInput component for number of members */}
                    <TextInput
                        label="Number of Members"
                        type="number"
                        min="1"
                        value={members}
                        onChange={(e) => setMembers(Number(e.target.value))} // Ensure to parse as a number
                        required
                    />

                    {/* Use TextInput for additional information as a textarea */}
                    <TextInput
                        label="Additional Information"
                        value={additionalInfo}
                        onChange={setAdditionalInfo}
                        type="textarea" // You can style this later in your TextInput component
                    />

                    {/* Loader component displayed while loading */}
                    {loading && <Loader />}

                    {/* Use Button component for form submission */}
                    <Button
                        label="Submit"
                        type="submit"
                    />
                </form>
            </div>

            {/* Add Footer component */}
            <Footer />
        </div>
    );
};

export default SessionForm;
